import React from 'react';

import Header from './Header.jsx';
import Footer from './Footer.jsx';
import Main from './Main.jsx';
import ImagePopup from './ImagePopup.jsx';
import EditProfilePopup from './EditProfilePopup.jsx';
import EditAvatarPopup from './EditAvatarPopup.jsx';
import AddPlacePopup from './AddPlacePopup.jsx';
import ConfirmCardDeletePopup from './ConfirmCardDelepePopup.jsx';
import api from '../utils/Api';
import CurrentUserContext from '../contexts/CurrentUserContext.js';
import { Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import ProtectedRoute from './ProtectedRoute.js';
import Register from './Register.jsx';
import Login from './Login.jsx';
import * as auth from '../utils/Auth.js';
import * as token from '../utils/token.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isConfirmPopupOpen, setIsConfirmPopupOpen] = React.useState(false);
  const [isInfoToolPopupOpen, setisInfoToolPopupOpen] = React.useState(true);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [selectedCardId, setSelectedCardId] = React.useState('');
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [userData, setUserData] = React.useState({});

  const navigate = useNavigate();

  React.useEffect(() => {
    api
      .getAppInfo()
      .then(([cardsData, userData]) => {
        setCards(cardsData);
        setCurrentUser(userData);
      })
      .catch((err) => console.log(err));
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
    setIsConfirmPopupOpen(false);
  }

  function handleCardClick(cardData) {
    setSelectedCard(cardData);
  }

  function handleCardLike(id) {
    api
      .addLike(id)
      .then((newCardData) => {
        setCards((state) =>
          state.map((card) => (card._id === id ? newCardData : card))
        );
      })
      .catch((err) => {
        console.log(`Handle like error:${err}`);
      });
  }

  function handleCardDislike(id) {
    api
      .deleteLike(id)
      .then((newCardData) => {
        setCards((state) =>
          state.map((card) => (card._id === id ? newCardData : card))
        );
      })
      .catch((err) => {
        console.log(`Handle dislike error:${err}`);
      });
  }

  function handleConfirmPopupOpen(id) {
    setIsConfirmPopupOpen(!isConfirmPopupOpen);
    setSelectedCardId(id);
  }

  function handleCardDelete(id) {
    api
      .deleteCard(id)
      .then((res) => {
        setCards((state) => state.filter((card) => card._id !== id));
        closeAllPopups();
      })
      .catch((err) => {
        console.log(`Delete card error:${err}`);
      });
  }

  function handleUpdateUser({ name, about }) {
    api
      .editProfile({ name, about })
      .then((newUserInfo) => {
        setCurrentUser(newUserInfo);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(`Profila update err:${err}`);
      });
  }

  function handleUpdateAvatar({ avatar }) {
    api
      .setAvatar(avatar)
      .then((newAvatarData) => {
        setCurrentUser(newAvatarData);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(`Avatar update err:${err}`);
      });
  }

  function handleAddPlaceSubmit({ link, name }) {
    api
      .addNewCard({ link, name })
      .then((newCardData) => {
        setCards([newCardData, ...cards]);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(`Add place confirmation error:${err}`);
      });
  }

  function handleRegister({ password, email }) {
    return auth.register(password, email).then((res) => {
      if (!res || res.statusCode === 400)
        throw new Error('Something went wrong with registration');
      return res;
    });
  }
  function getGwt(jwt) {
    return auth
      .getContent(jwt)
      .then((res) => {
        if (res.statusCode === 401) throw new Error('Unauthorized user');
        return res;
      })
      .catch((error) => console.log(`Error during token check:${error} `));
  }

  React.useEffect(() => {
    //tockenCeck
    const jwt = token.getToken();
    if (jwt) {
      getGwt(jwt)
        .then((res) => {
          setLoggedIn(true);
          setUserData({ email: res.email, id: res._id });
        })
        .catch((err) => {
          console.log('Error during token validation:', err);
          token.removeToken();
        });
    }
  }, []);

  React.useEffect(() => {
    if (loggedIn) {
      navigate('/');
    }
  }, [loggedIn]);

  function handleLogin({ password, email }) {
    return auth.login(password, email).then((res) => {
      console.log(res);
      if (!res) throw new Error('Wrong email or password');
      if (res) {
        setLoggedIn(true);
        token.setToken(res.jwt);
      }
    });
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <div className='page'>
          <Header />
          <Routes>
            <Route
              path='/'
              element={
                <ProtectedRoute
                  element={Main}
                  loggedIn={loggedIn}
                  onEditProfile={handleEditProfileClick}
                  onAddPlace={handleAddPlaceClick}
                  onEditAvatar={handleEditAvatarClick}
                  onCardClick={handleCardClick}
                  onLikeClick={handleCardLike}
                  onDislikeClick={handleCardDislike}
                  onCardDelete={handleConfirmPopupOpen}
                  cards={cards}
                />
              }
            />
            <Route
              path='/sign-up'
              element={<Register onRegister={handleRegister} />}
            />
            <Route path='/sign-in' element={<Login onLogin={handleLogin} />} />
            <Route
              path='*'
              element={
                loggedIn ? (
                  <Navigate to='/' replace />
                ) : (
                  <Navigate to='/sign-in' replace />
                )
              }
            />
          </Routes>
          {loggedIn && <Footer />}
          <InfoTooltip
            isOpened={isInfoToolPopupOpen}
            onClose={closeAllPopups}
          />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onAddPlace={handleAddPlaceSubmit}
          ></AddPlacePopup>
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />
          <ConfirmCardDeletePopup
            isOpen={isConfirmPopupOpen}
            onClose={closeAllPopups}
            onDeleteCard={handleCardDelete}
            selectedCardId={selectedCardId}
          />
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
