import React from 'react';

import Header from './Header.jsx';
import Footer from './Footer.jsx';
import Main from './Main.jsx';
import PopupWithForm from './PopupWithForm.jsx';
import ImagePopup from './ImagePopup.jsx';
import api from '../utils/Api';
import CurrentUserContext from '../contexts/CurrentUserContext.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        console.log(data);
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log(err);
      });

    api
      .getInitialCards()
      .then((cardsData) => {
        setCards(cardsData);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(cardData) {
    console.log(cardData);
    setSelectedCard(cardData);
  }

  function handleCardLike(id) {
    api.addLike(id).then((newCardData) => {
      const newCards = cards.map(
        (card) => (card._id = id ? newCardData : card)
      );
      setCards(newCards);
    });
    // Снова проверяем, есть ли уже лайк на этой карточке
    //const isLiked = card.likes.some((item) => item._id === currentUser._id);

    /*const likePromise = isLiked
      ? api.deleteLike(card._id)
      : api.addLike(card._id);*/

    // Отправляем запрос в API и получаем обновлённые данные карточки
    //likePromise.then((newCard) => {
    //setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    //});
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <div className='page'>
          <Header />
          <Main
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            onLikeClick={handleCardLike}
          />
          <Footer />
          <PopupWithForm
            name='profile'
            title='Редактировать профиль'
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            buttonText='Сохранить'
          >
            <input
              type='text'
              name='name'
              defaultValue='Жак-Ив Кусто'
              placeholder='Имя'
              className='popup__input popup__input_type_name'
              id='name-input'
              required
              minLength='2'
              maxLength='40'
            />
            <span className='name-input-error popup__error'></span>
            <input
              type='text'
              name='about'
              defaultValue='Исследователь океана'
              placeholder='o себе'
              className='popup__input popup__input_type_about'
              id='about-input'
              required
              minLength='2'
              maxLength='200'
            />
            <span className='about-input-error popup__error'></span>
          </PopupWithForm>
          <PopupWithForm
            name='card'
            title='Новое место'
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            buttonText='Создать'
          >
            <input
              type='text'
              name='name'
              placeholder='Название'
              className='popup__input popup__input_type_place'
              id='place-input'
              required
              minLength='2'
              maxLength='30'
            />
            <span className='place-input-error popup__error'></span>
            <input
              type='url'
              name='link'
              placeholder='Ссылка на картинку'
              className='popup__input popup__input_type_link'
              id='link-input'
              required
            />
            <span className='link-input-error popup__error'></span>
          </PopupWithForm>
          <PopupWithForm
            name='avatar'
            title='Обновить аватар'
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            buttonText='Сохранить'
          >
            <input
              type='url'
              name='link'
              placeholder='Ссылка на аватар'
              className='popup__input popup__input_type_link'
              id='avatar-input'
              required
            />
            <span className='avatar-input-error popup__error'></span>
          </PopupWithForm>
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
