import React from 'react';

import Header from './Header.jsx';
import Footer from './Footer.jsx';
import Main from './Main.jsx';
import PopupWithForm from './PopupWithForm.jsx';
import ImagePopup from './ImagePopup.jsx';
import EditProfilePopup from './EditProfilePopup.jsx';
import EditAvatarPopup from './EditAvatarPopup.jsx';
import api from '../utils/Api';
import CurrentUserContext from '../contexts/CurrentUserContext.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        console.log(data);
        setCurrentUser(data);
      })
      .catch((err) => {
        console.log(err);
      });

    api
      .getInitialCards()
      .then((cardsData) => {
        setCards(cardsData);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  function handleCardClick(cardData) {
    console.log(cardData);
    setSelectedCard(cardData);
  }

  function handleCardLike(id) {
    api
      .addLike(id)
      .then((newCardData) => {
        const newCards = cards.map((card) =>
          card._id === id ? newCardData : card
        );
        setCards(newCards);
      })
      .catch((err) => {
        console.log(`Handle like error:${err}`);
      });
  }

  function handleCardDislike(id) {
    api
      .deleteLike(id)
      .then((newCardData) => {
        const newCards = cards.map((card) =>
          card._id === id ? newCardData : card
        );
        setCards(newCards);
      })
      .catch((err) => {
        console.log(`Handle dislike error:${err}`);
      });
  }

  function handleCardDelete(id) {
    api
      .deleteCard(id)
      .then(() => {
        const newCards = cards.filter((card) => card._id !== id);
        setCards(newCards);
      })
      .catch((err) => {
        console.log(`Delete card error:${err}`);
      });
  }

  function handleUpdateUser({ name, about }) {
    api.editProfile({ name, about }).then((newUserInfo) => {
      setCurrentUser(newUserInfo);
      closeAllPopups();
    });
  }

  function handleUpdateAvatar({ avatar }) {
    api.setAvatar({ link }).then((data) => console.log(data));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='body'>
        <div className='page'>
          <Header />
          <Main
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            onLikeClick={handleCardLike}
            onDislikeClick={handleCardDislike}
            onCardDelete={handleCardDelete}
            cards={cards}
          />
          <Footer />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <PopupWithForm
            name='card'
            title='Новое место'
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            buttonText='Создать'
          >
            <input
              type='text'
              name='name'
              placeholder='Название'
              className='popup__input popup__input_type_place'
              id='place-input'
              required
              minLength='2'
              maxLength='30'
            />
            <span className='place-input-error popup__error'></span>
            <input
              type='url'
              name='link'
              placeholder='Ссылка на картинку'
              className='popup__input popup__input_type_link'
              id='link-input'
              required
            />
            <span className='link-input-error popup__error'></span>
          </PopupWithForm>
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />

          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
