import React from 'react';
import CurrentUserContext from '../contexts/CurrentUserContext';

function Card({ onCardClick, link, name, likes }) {
  const currentUser = React.useContext(CurrentUserContext);

  const handleCardClick = () => {
    onCardClick({ link, name });
  };

  // Определяем, являемся ли currentUser владельцем текущей карточки
  const isOwn = cardOwner === currentUser._id;

  // Определяем, есть ли у карточки лайк, поставленный текущим пользователем

  return (
    <article className='element'>
      <div className='element__image-container'>
        <img
          className='element__image'
          src={link}
          alt={name}
          onClick={handleCardClick}
        />
      </div>
      {isOwn && <button type='button' className='element__delete-btn'></button>}
      <div className='element__wrapper'>
        <h2 className='element__place-name'>{name}</h2>
        <div className='element__like-container'>
          <button type='button' className='element__like-btn button'></button>
          <p className='element__likes-number'>{likes}</p>
        </div>
      </div>
    </article>
  );
}

export default Card;
